import mongoose from "mongoose";

const userSchema = new mongoose.Schema({
    username: String,
    name: String,
    passwordHash: String,

    token: String,
    url: [{
        longUrl: String,
        shortUrl: String
}],
      //
    activationStatus: {
        type: String,
        // enum:[type],
        default: "Inactive"
    }
})


export default mongoose.model('User', userSchema, 'users');